import 'react-native-gesture-handler';
import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button, TouchableOpacity} from 'react-native';

 var data = [
    {
        question: 'I know who I am, the marketable skills I possess, and the job that I want.',
        response: "True"
    },
    {
        question: 'I would like to learn how to research: (Select all that apply)'
    },
    {
        question: 'Regarding my resume: (Only check one answer)'
    },
    {
        question: 'I could use help with: (Select all that apply)'
    },
    {
        question: 'Select True (Thumbs Up) or False (Thumbs Down) for the following statements:'
    },
    {},
  ];
  
const InitialQuestionnaire = ({navigation}) => {
 const [questionNum, setQuestionNum] = useState(1);
  const [questionText, setQuestionText] = useState(data[0].question);
  const [response, getQuestionResponse] = useState();
  const responses = new Array(8);

  const ReviewQuestionnaire = ({qNum}) => {
    return (
        <View style = {styles.reviewQuestionnaireContainer}>
            <Text> Question {qNum} </Text>
            <Text> Question 1.........</Text>
            <View style = {styles.buttonContainer}>
                <Button title = "Agree"/>
            </View>
        </View>
    )
  }


  const QuestionOption = ({numOptions, qNum}) => {



  }

  const changeQuestion = () => {
        setQuestionText(data.question[questionNum-1]);
  }

  const nextQuestion = () => {
    if (questionNum < 8) {
        setQuestionNum(questionNum+1);
        changeQuestion();
    }
    else {
        navigation.navigate('ReviewQuestionnaire')
    }
  }
  const prevQuestion = () => {
    if (questionNum > 1) {
        setQuestionNum(questionNum-1);
        changeQuestion();
    }
  }
  const questionResponse = (response) => {
    responses[questionNum] = response;
  //store user's response to question
  }

  return (
    <View style={styles.container}>

      <Text> Question #{ questionNum } </Text>

      <View style = {styles.questionContainer}>
        <Text> {questionText} </Text>
      </View>
      <TouchableOpacity
              style = {styles.buttonContainer}
              onPress={(event) => questionResponse("Strongly Disagree")}>
              <Text>Strongly Disagree</Text>
            </TouchableOpacity>
      <TouchableOpacity
              style = {styles.buttonContainer}
              onPress={(event) => questionResponse("Strongly Disagree")}>
              <Text>Strongly Disagree</Text>
            </TouchableOpacity>
      <TouchableOpacity
              style = {styles.buttonContainer}
              onPress={(event) => questionResponse("Strongly Disagree")}>
              <Text>Strongly Disagree</Text>
            </TouchableOpacity>
      <TouchableOpacity
        style = {styles.buttonContainer}
        onPress={(event) => questionResponse("Strongly Disagree")}>
        <Text>Strongly Disagree</Text>
      </TouchableOpacity>
      {/*<View style={{ flexDirection: 'row', margin: 10 }}>
            <View style = {styles.navBtnContainer}>
                <Button
                  onPress={prevQuestion}
                  title="Back"
                />
            </View>

            <View style = {styles.navBtnContainer}>
                <Button
                  onPress={nextQuestion}
                  title="Next"
                />
            </View>
      </View>*/}

       <TouchableOpacity
           style = {[styles.defaultButton, {padding: 10}]}
           onPress={() => navigation.navigate('')}>
           <Text style = {styles.textButton}> Submit </Text>
       </TouchableOpacity>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#EFEFEF',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 40
   },
   contentContainer: {
    padding: 20
   },
   button: {
      padding: 20,
      fontSize: 15,
      fontFamily: "arial",
      textAlign: "center",
      color: "white",
      borderRadius: 20,
      borderWidth: 1,
      borderColor: '#fff',
    },
    buttonContainer: {
      width: "20%",
      margin: 10,
      backgroundColor: "white",
      borderRadius: 40,
      alignItems: "center",
      textAlign: "center",
      padding: 20,
      borderWidth: 2,
      borderColor: "#004473"
    },
    questionContainer: {
        width: "70%",
        margin: 20,
        alignItems: "center",
        padding: 10
    },
    navBtnContainer: {
        margin: 20
    },
    reviewQuestionnaireContainer: {
        backgroundColor: "#FFF",
        padding: 10,
        width: "30%",
        borderColor: "#004473",
        borderRadius: 20,
        alignItems: "center",
        margin: 10
    },
    defaultButton: {
        backgroundColor: "#004473",
        width: "15%",
        margin: 20,
        marginTop:30
    },
    textButton: {
        color: "white",
        fontWeight: "bold",
        alignItems: "center",
        textAlign: "center",
        fontSize: 18
    },
});

export default InitialQuestionnaire;
