import React, { useState } from 'react';
import 'react-native-gesture-handler';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button} from 'react-native';

export default function App() {
  const [questionNum, setQuestionNum] = useState(1);
  const [questionText, setQuestionText] = useState();
  const [response, getQuestionResponse] = useState();

  const changeQuestion = () => {
       if (questionNum == 1)
          setQuestionText("I feel totally comfortable in my understanding of: {\n'} • What employers want in an employee • The tools I need to conduct an effective job search • Goal setting and am able to set effective goals • Setting a schedule for my daily tasks during job search • How to dress appropriately for the job search");
       else
          setQuestionText("This is not Question 1");
  }

  const nextQuestion = () => {
    setQuestionNum(questionNum+1);
    changeQuestion();
  }
  const prevQuestion = () => {
    setQuestionNum(questionNum-1);
    changeQuestion();
  }
  const questionResponse = (response) => {
  //store user's response to question
  }

  return (
    <View style={styles.container}>
      <Text>Question { questionNum } of 8</Text>

      <View style = {styles.questionContainer}>
        <Text> {questionText} </Text>
      </View>
      <View style = {styles.buttonContainer}>
          <Button
            onPress={(event) => questionResponse("Strongly Agree")}
            title="Strongly Agree"
          />
      </View>
      <View style = {styles.buttonContainer}>
          <Button
            onPress={(event) => questionResponse("Agree")}
            title="Agree"
          />
      </View>
      <View style = {styles.buttonContainer}>
          <Button
            onPress={(event) => questionResponse("Disagree")}
            title="Disagree"
          />
      </View>
      <View style = {styles.buttonContainer}>
          <Button
            onPress={(event) => questionResponse("Strongly Disagree")}
            title="Strongly Disagree"
          />
      </View>
      <View style={{ flexDirection: 'row', alignItems: 'center'}}>
            <View style = {styles.navBtnContainer}>
                <Button
                  onPress={nextQuestion}
                  title="Back"
                />
            </View>

            <View style = {styles.navBtnContainer}>
                <Button
                  onPress={prevQuestion}
                  title="Next"
                />
            </View>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
   },
   button: {
      padding: 20,
      fontSize: 15,
      fontFamily: "arial",
      textAlign: "center",
      color: "#0061A5",
      borderRadius: 10,
      borderWidth: 1,
      borderColor: '#fff',
    },
    buttonContainer: {
      width: "20%",
      margin: 10,
      backgroundColor: "#004473"
    },
    questionContainer: {
        width: "70%",
        margin: 20
    },
    navBtnContainer: {
        width: "70%",
        margin: 20,
    }
});
