import 'react-native-gesture-handler';
import React, {useState} from 'react';
import {SafeAreaView, StyleSheet, ScrollView, View, Text, StatusBar, Button, TextInput, TouchableOpacity, Image, KeyboardAvoidingView, FlatList, ImageBackground} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';

const LIST_DATA = new Array(8).fill('');
const CARD_RADIUS = 8;

const Curriculum = ({navigation}) => {

const Card = ({title, letter}) => (
  <View style={styles.cardWrapper}>
    <View style = {styles.cardImageWrapper}>
        <ImageBackground
            source={require('./images/square.png')}
            style={styles.cardImage}
        />
    </View>
    <View style={styles.cardTextWrapper}>
      <Text style = {styles.headerText}> Title {title} </Text>
    </View>
    <TouchableOpacity style={styles.buttonCard}>
        <Text style = {styles.textButtonCard}> -> </Text>
    </TouchableOpacity>
  </View>
);

  return (
    <>
      <StatusBar barStyle="dark-content" />
      <SafeAreaView>
         <FlatList
            data={LIST_DATA}
            renderItem={() => <Card />}
            keyExtractor={(_, index) => index.toString()}
          />
      </SafeAreaView>
    </>
  );
};

const styles = StyleSheet.create({
  scrollView: {
    padding: 20
  },
  container: {
    alignItems: "center",
    width: "80%"
  },
  engine: {
    position: 'absolute',
    right: 0,
  },
  sectionContainer: {
    marginTop: 32,
    paddingHorizontal: 24,
  },
  logo: {
    width: 50,
    height: 60,
    margin: 10,
    padding: 10,
  },
  title: {
    fontSize: 40,
    fontWeight: "bold",
    color: "#022169",
    textAlign: "center",
    marginTop: 40
  },
  defaultText: {
    textAlign: "center",
    color: "#022169",
    fontSize: 16,
    padding: 20
  },
  defaultText2: {
      textAlign: "center",
      color: "#022169",
      fontSize: 20,
      padding: 20
  },
  headerText: {
    fontSize:20,
    fontWeight: "bold",
    textAlign: "center"
  },
  myClubsPhoto: {
    width: 100,
    height: 150
  },
  myClubsHeader: {
    backgroundColor: "#B3B9BC"
  },
    cardWrapper: {
      height: 150,
      margin: 10,
      flexDirection: 'row',
      alignItems: 'center',
      borderRadius: CARD_RADIUS,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 5,
      },
      shadowOpacity: 0.36,
      shadowRadius: 6.68,
      elevation: 11,
      backgroundColor: '#fff',
    },
    cardImageWrapper: {
      flex: 1,
      overflow: 'hidden',
      borderTopLeftRadius: CARD_RADIUS,
      borderBottomLeftRadius: CARD_RADIUS,
    },
    cardImage: {
      flex: 1,
      resizeMode: 'cover',
      justifyContent: 'center',
      height: 150,
    },
    cardTextWrapper: {
      flex: 1,
      paddingLeft: 10,
      flexDirection: 'row',
      justifyContent: 'flex-start',
    },
    curriculumIcon: {
        backgroundColor: "red"
    },
    buttonCard: {
        borderRadius: 10,
        backgroundColor: "#004473",
        height: 25,
        marginRight: 10
    },
    textButtonCard: {
        color: "white"
    }

});

export default Curriculum;
